"use client";

import { motion, AnimatePresence } from "framer-motion";
import { useEffect, useState } from "react";
import Image from "next/image";

export default function IntroPage() {
  // Initialiser à false pour éviter le rendu SSR
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Vérifier côté client uniquement si l'intro a déjà été vue
    const hasSeenIntro = sessionStorage.getItem("hasSeenIntro");

    if (!hasSeenIntro) {
      // Afficher l'intro immédiatement
      setIsVisible(true);

      // Marquer comme vu immédiatement pour éviter les réaffichages
      sessionStorage.setItem("hasSeenIntro", "true");

      // Masquer après 3.5 secondes
      const timer = setTimeout(() => {
        setIsVisible(false);
      }, 3500);

      return () => clearTimeout(timer);
    }
  }, []);

  // Ne rien afficher si pas visible
  if (!isVisible) {
    return null;
  }

  return (
    <AnimatePresence mode="wait">
      {isVisible && (
        <motion.div
          initial={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          transition={{ duration: 0.8, ease: "easeInOut" }}
          className="fixed inset-0 z-[9999] flex items-center justify-center bg-white"
          style={{ backgroundColor: "#FFFFFF" }}
        >
          <div className="relative flex items-center justify-center">
            {/* Logo sans aiguilles */}
            <div className="relative">
              <Image
                src="/logo-sans-aiguilles.png"
                alt="Maison du Temps"
                width={400}
                height={400}
                className="w-64 h-64 md:w-80 md:h-80 lg:w-96 lg:h-96 object-contain"
                priority
              />

              {/* Aiguille noire animée - rotation anti-horaire depuis le centre */}
              <motion.div
                className="absolute top-1/2 left-1/2 origin-center -translate-x-1/2 -translate-y-1/2"
                style={{
                  width: "1.5px",
                  height: "16%",
                  backgroundColor: "#000000",
                  boxShadow: "0 0 1px rgba(0, 0, 0, 0.3)",
                }}
                animate={{
                  rotate: -360, // Rotation anti-horaire (négatif)
                }}
                transition={{
                  duration: 4,
                  ease: "linear",
                  repeat: Infinity,
                }}
              >
                {/* Pointe fine de l'aiguille */}
                <div
                  className="absolute top-0 left-1/2 -translate-x-1/2 -translate-y-full"
                  style={{
                    width: 0,
                    height: 0,
                    borderLeft: "2px solid transparent",
                    borderRight: "2px solid transparent",
                    borderBottom: "6px solid #000000",
                  }}
                />
              </motion.div>

              {/* Point central de l'aiguille */}
              <div
                className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 rounded-full z-10"
                style={{
                  width: "6px",
                  height: "6px",
                  backgroundColor: "#000000",
                  boxShadow: "0 0 2px rgba(0, 0, 0, 0.4)",
                }}
              />
            </div>
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}

