        showVideo={false}
      />

      <section className="py-24">
        <div className="container-palace">
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            {corpsServices.map((service, index) => (
              <ServiceCard
                key={service.slug}
